import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

export default function PostPage() {
  const { id } = useParams();
  const [post, setPost] = useState(null);

  useEffect(() => {
    const fetchPost = async () => {
      try {
        const response = await fetch(`http://localhost:4000/post/${id}`);
        if (response.ok) {
          const data = await response.json();
          setPost(data);
        } else {
          throw new Error("Post not found"); // Özel bir hata oluşturabilirsiniz
        }
      } catch (error) {
        console.log(error);
      }
    };
  
    fetchPost();
  }, [id]);
  

  if (!post) {
    return <div>Loading...</div>;
  }

  const { title, summary, cover, content } = post;

  return (
    <center>
    <div>
      <div>
        <img src={`http://localhost:4000/${cover}`} alt="" style={{ maxWidth: "500px", height: "auto" }} />
      </div>
      <h1 style={{ textAlign:"left" }}>{title}</h1>
      <p>{summary}</p>
      <div>{content}</div>
    </div>
    </center>
  );
}
