import ReactQuill from "react-quill";
import "react-quill/dist/quill.snow.css";
import { useState } from "react";

export default function CreatePost() {
  const [title, setTitle] = useState("");
  const [summary, setSummary] = useState("");
  const [content, setContent] = useState("");
  const [files, setFiles] = useState("");

  function createNewPost(ev) {
    ev.preventDefault();
    
    const data = new FormData();
    data.set('title', title);
    data.set('summary', summary);
    data.set('content', content);
    data.append('file', files[0]); // Dosyayı FormData'ya eklemek için append kullanılmalı ve files[0] olarak alınmalı.
    
    console.log(data);
    
    // fetch('http://localhost:4000/post', {
    //   method: 'POST',
    //   body: data
    // })
  }

  return (
    <form onSubmit={createNewPost}>
      <input
        type="text" // type="title" yerine type="text" kullanmalısınız.
        placeholder="Title"
        value={title}
        onChange={(ev) => setTitle(ev.target.value)}
      />
      <input
        type="text" // type="summary" yerine type="text" kullanmalısınız.
        placeholder="Summary"
        value={summary}
        onChange={(ev) => setSummary(ev.target.value)}
      />
      <input
        type="file"
        onChange={(ev) => setFiles(ev.target.files)}
      />
      <button style={{ marginTop: "5px" }}>Create post</button>
    </form>
  );
}
